    state = {
        count: 0,
        timer: 0
    }

    onIncrease = () => {
        this.setState((oldState) => {
            return {
                count: oldState.count + 1
            }
        })
    }
    onDecrease = () => {
        this.setState((oldState) => {
            return {
                count: oldState.count - 1
            }
        })
    }

